import { fromJS } from 'immutable';
const defaultState = fromJS({
    articleList: [],
    recommedList: []
})
export default (state = defaultState, action) => {
    switch (action.type) {
        case 'change_home_data':
            return state.merge({
                articleList: fromJS(action.articleList),
                recommedList: fromJS(action.recommedList),
            })
            console.log(action);
        default:
        return state;
    }
    
}
