import styled from 'styled-components'
import logoPic from '../../statics/logo.png'
export const HeadrerWrapper = styled.div`
    position:relative;
    height:56px;
    border-bottom:solid 1px #f0f0f0;
`;
export const Logo = styled.a`
    height:56px;
    width:100px;
    position:absolute;
    top:0;
    left:0;
    display:block;
    background:url(${logoPic});
    background-size:contain;
`;
export const Nav = styled.div`
    width:960px;
    height:100%;
    margin:0 auto;
    padding-right:60px;
    /*保证宽度不变*/
    box-sizing:border-box;
`;
export const NavItem = styled.div`
    line-height:56px;
    padding: 0 15px;
    font-size:17px;
    color:#333;
    &.left {
        float:left;
    }
    &.right {
        float:right;
        color:#969696;
    }
    &.active {
        color:#ea6f5a;
    }
    `;
export const SearchWrapper = styled.div`
    float:left;
    position:relative;
    .zoom{
        position:absolute;
        right:5px;
        bottom:5px;
        width:30px;
        line-height:30px;
        border-radius:15px;
        text-align:center;
        &.focused{
            background:#777;
            color:#fff;
        }
    }
`;
export const NavSearch = styled.input.attrs({
    placeholder: '搜索'
})`
    width:160px;
    height:38px;
    border:none;
    outline:none;
    border-radius:19px;
    background:#eee;
    margin-top:9px;
    margin-left:20px;
    padding:0 30px 0 20px;
    box-sizing:border-box;
    font-size:14px;
    color:#666;
    /*当前组件下的placeholder*/
    &::placeholder{
        color:#999;
    }
    &.focused{
        width:240px;
    };
    .slide-enter{
        width:160px;
        transition:all 0.2s ease-out;
    }
    &.slide-enter-active{
        width:240px;
    }
    &.slide-exit{
        transition:all 0.2s ease-out;
    }
    &.slide-exit-active{
        width:160px;
    }

 `;
export const SearchInfo = styled.div`
    position:absolute;
    left:0;
    top:56px;
    width:220px;
    padding: 0 20px;
    box-shadow:0 0  8px rgba(0,0,0,.2);
    background:white;
 `;

 export const SearchInfoTitle = styled.div`
    margin-top:20px;
    margin-bottom:15px;
    line-height:20px;
    font-size:14px;
    color:#969696;
 `;

export const SearchInfoSwitch = styled.span`
    float:right;
    font-size:13px;
    .spin{
        display:block;
        float:left;
        font-size:12px;
        margin-right:2px;
        /*让其所有的动画效果都是0.2s执行完毕*/
        transition:all .2s ease-in;
        transform:rotate(0deg);
        /*以自己的中心为旋转中心*/
        transform-origin:center center;
    }
`;
export const SearchInfoList = styled.div`
    overflow:hidden;
`;
export const SearchInfoItem = styled.a`
    display:block;
    float:left;
    line-height: 20px;
    font-size: 12px;
    padding: 0 5px;
    margin-right:10px;
    margin-bottom:15px;
    border:1px solid #ddd;
    color:#787878;
    border-radius:3px;
`;
export const Addition = styled.div`
    position:absolute;
    right:0;
    top: 0;
    height:56px;
 ` ;

export const Button = styled.button`
    float:right;
    margin-top:9px;
    margin-right:20px;
    padding:0 20px;
    line-height:38px;
    border-radius:19px;
    border:1px solid #ec6149;
    font-size:14px;
    &.reg{
        color:#ec6149;
        background:white;

    }
    &.writting{
        color:#fff;
        background:#ec6149;
    };
 `
